import React,{Component} from 'react';
import './Components/style.css';

class TempatWisata extends Component{
    constructor(props){
        super(props)
        this.state = {
            list_wisata : this.props.render_list_wisata(this.props.id)
        }
    }
    
    render(){
        let {judul,lokasi,harga,urlimage,deskripsi} = 'Maaf halaman tidak ditemukan';

        if({judul,lokasi,harga,urlimage,deskripsi} = this.state.list_wisata){
            let judul = this.state.list_wisata.nama_tempat_wisata;
            let lokasi = this.state.list_wisata.lokasi;
            let harga = this.state.list_wisata.harga;
            let urlimage = this.state.list_wisata.urlimage;
            let deskripsi = this.state.list_wisata.deskripsi;
        }
        return(
            <container>
                <h1>{judul}</h1>
                <img src={'http://localhost:4000/images' + urlimage} />
                <h4>{lokasi}</h4>
                <h4>{harga}</h4>
                <p>{deskripsi}</p>
            </container>
        )
    }
}

class LandingPage extends Component{
    constructor(props){
        super(props)
        this.state = {}
    }

    getListWisata(id,req,res){
        fetch(`http://localhost:4000/json/${id}`,{
            method : 'GET',
            headers : {
                'Content-Type' : 'application/json',
                'Access-Allow-Control-Origin' : '*'
            }
        })
        .then(data => res.json(data))
        .catch(err => res.send(err))
    }

    render(){
        return(
            <container>
                <TempatWisata render_list_wisata={id => this.getListWisata(id)} />
            </container>
        );
    }
}

export default LandingPage;
