import React, { Component } from "react";
import gif_loading from "./img/loading_button.gif";

export default class ButtonLoader extends Component {
  state = {
    loading: false
  };

  fetchData = () => {
    this.setState({ loading: true });

    //Faking API call here
    setTimeout(() => {
      this.setState({ loading: false });
    }, 2000);
  };

  render() {
    const { loading } = this.state;

    return (
      <div style={{ marginTop: "60px" }}>
        <button className="klik_send_contact" onClick={this.fetchData} disabled={loading} type="submit">
            {loading && (<img src={gif_loading} className="loading" alt="" />)}
            {loading && <span>{this.props.text}</span>}
            {!loading && <span>{this.props.text_loading}</span>}
        </button>
      </div>
    );
  }
}
