import React,{Component} from "react";
import Header from "./Components/Header";
import Footer from "./Components/Footer";
import "./Components/style.css";

class titipanBarang extends Component{
    constructor(props){
        super(props)
        this.state = {
            jumlah_barang : 0            
        }
    }
    /*
    list_jumlah=()=>{
        this.setState({jumlah_barang:jumlah_barang});
        let jumlah_barang=parseInt(jumlah_barang);

        for (let i=0; i<jumlah_barang; i++){
            let elemen = document.createElement("input");
            let spasi = document.createElement("br");
            elemen.type = "text";
            list_input.appendChild(elemen);
            list_input.appendChild(spasi);
        }
    }
    */
    render(){
        list_jumlah=()=>{
            let list_input = document.getElementById("test");
            let list_barang = this.state.jumlah_barang;
            let barang = parseInt(list_barang);

            for (let i = 0; i<barang; i++){
                let elemen = document.createElement("input");
                let spasi = document.createElement("br");
                elemen.type = "text";
                list_input.appendChild(elemen);
                list_input.appendChild(spasi);
            }
        }
        return(
            <div>
                <Header />
                <div class="request-list">
                    <h1>List titipan barang</h1>
                    <form action="list_barang.html" method="POST">
                        <label>Berapa banyak barang yang mau anda titip?</label><br />
                        <input type="number" name={this.state.jumlah_barang} />
                        <button type="button" onClick={list_jumlah}>Oke</button>
                    </form>
                    <div id="test"></div>
                </div>
                <Footer />
            </div>
        );
    }
}

export default titipanBarang;
